import React, { useState } from "react"
import axios from "axios"

const ProfileInfo = (props) => {
    const [follow, setFollow] = useState(true)
    const user = useSe

    const handleFollow = async() => {
        setFollow(!follow)
        const rs = await axios.post('/users/follow',{follow: props.user.id})
    }
    const handUnleFollow = () => {
        setFollow(!follow)
    }

    return (
        <div className=" row">
            <div className=" col-xxl-6 m-auto">
                <div className="card mb-3 shadow-sm p-3 border border-primary rounded">
                    <div className="card-body">
                        <h5 className="card-title">{props.user.displayname}</h5>
                        <p className="card-text">{props.user.bio}</p>
                        <div className=" mb-3">
                            <div className="card-text"><small className="text-muted">1000 followers</small></div>
                            {follow ? <button onClick={handleFollow} className="btn btn-outline-primary float-end">Follow</button>
                                : <button onClick={handUnleFollow} className="btn btn-outline-primary float-end">Unfollow</button>}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileInfo
