import axios from "axios"
import React, { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import { useParams, Navigate } from 'react-router-dom'
import CreatePost from "../../components/CreatePost"
import ListPosts from "../../components/ListPosts"
import ProfileInfo from "../../components/ProfileInfo"



const ProfilePage = () => {
    const user = useSelector(state => state.user)
    const params = useParams()
    const username = params.username
    const [profile, setProfile] = useState({})
    const [follow, setFollow] = useState(true)
    // example
    const data = [
        {
            id: 1,
            displayName: 'John Doe',
            createdAt: '2020-01-01',
            content: 'day la bai dang mau',
            likes: 0,
            comments: 0,
        }, {
            id: 2,
            displayName: 'John Doe',
            createdAt: '2020-01-01',
            content: 'day la bai dang mau',
            likes: 0,
            comments: 0,
        }, {
            id: 3,
            displayName: 'John Doe',
            createdAt: '2020-01-01',
            content: 'day la bai dang mau',
            likes: 0,
            comments: 0,
        }, {
            id: 4,
            displayName: 'John Doe',
            createdAt: '2020-01-01',
            content: 'day la bai dang mau',
            likes: 0,
            comments: 0,
        }
    ]

    useEffect(() => {
        const fetchdata = async () => {
            const rs = await axios.post('/users/profile', { username })
            if (rs.data.success) {
                console.log(rs.data.user)
                setProfile(rs.data.user)
            } else {
                console.log(rs.data.message)
            }
        }
        fetchdata()
    }, [username])


    if (user.id === null) return <Navigate to='/login' />
    return (
        <>
            <div className=" row">
                <div className=" col-xxl-6 m-auto">
                    <div className="card mb-3 shadow-sm p-3 border border-primary rounded">
                        {profile !== {} && <div className="card-body">
                            <h5 className="card-title">{profile.displayname}</h5>
                            <p className="card-text">{profile.bio}</p>
                            <div className=" mb-3">
                                <div className="card-text"><small className="text-muted">{profile.following} followers</small></div>
                                {
                                    user.id !== profile.username ? (profile.following.includes(user.username) ? <button className="btn btn-outline-primary float-end">Follow</button>
                                        : <button className="btn btn-outline-primary float-end">Unfollow</button>) : null
                                }


                            </div>
                        </div>}
                    </div>
                </div>
            </div>

            <CreatePost />
            <ListPosts posts={data} />
        </>
    )
}


export default ProfilePage
