import React from "react"
import { useNavigate } from 'react-router-dom'
import ListImages from "../ListImages"


const ListPosts = ({ posts }) => {
    const navigate = useNavigate()

    const ItemPost = posts.map((post) => {
        const handleClick = (id) => {
            navigate(`/posts/${id}`)
        }

        return (
            <div onClick={() => handleClick(post.id)} className=" card shadow-sm p-3 border border-primary rounded mb-3" key={post.id}>
                    <div className=" card-body">
                        <h5 className="card-title">{post.displayName}</h5>
                        <p className="card-text">{post.createdAt}</p>
                        <p className="card-text">{post.content}</p>
                        <ListImages images={post.images} />
                        <p className="card-text">{post.likes} thich - {post.comments} binh luan</p>
                    </div>
                    <div className=" card-footer bg-white">
                        <div className=" input-group">
                            <input type="text" className="form-control border border-primary rounded" placeholder="binh luan ..." />
                            <button className="btn btn-primary">Gui</button>
                        </div>
                    </div>
            </div>)
    })


    return (
        <div className="row">
            <div className="col-xxl-6 m-auto">
                {ItemPost}
            </div>
        </div>
    )
}


export default ListPosts
