let init = JSON. sessionStorage.getItem('user');
if (!init) init = {
    id: null
};
const userReducer = (state = init, action) => {
    switch (action.type) {
        case 'SET_USER':
            sessionStorage.setItem('user', JSON.stringify(action.payload));
            return action.payload;

        default:
            return state;
    }
}

export default userReducer;
