import React, { useState } from "react"

const ListComments = ({ comments }) => {

    const ItemComment = comments.map((comment, index) => {
        return (
            <div key={index} className=" card shadow-sm p-3">
                <div className=" card-body bg-light">
                    <div className=" card-title">{comment.displayName}</div>
                    <div className=" card-subtitle">{comment.createdAt}</div>
                    <div className=" card-text">{comment.content}</div>
                    <hr class=" py-0" />
                    <div className="card-subtitle text-primary"><span className=" text-muted">{comment.likes} thich</span></div>
                    <button className="btn btnn-outline-primary">Thich</button>
                </div>
            </div>
        )
    })


    return (
        <div className=" row">
            <div className=" col-xxl-6 m-auto">
                {ItemComment}
            </div>
        </div>
    )
}

export default ListComments
