import axios from "axios"
import React, { useEffect } from "react"
import { useSelector } from "react-redux"

const ProfileInfo = ({ profile }) => {
    const userCurrent = useSelector(state => state.user)

    useEffect(() => { }, [])

    console.log(profile)
    const handleFollow = async() => {
        const rs = await axios.post('/users/follow', { username: userCurrent.username, following: profile.username })
        if (rs.data.success) {
            console.log('follow')
        } else {
            console.log(rs.data.message)
        }
    }

    return (
        <div className=" row">
            <div className=" col-xxl-6 m-auto">
                <div className="card mb-3 shadow-sm p-3 border border-primary rounded">
                    <div className="card-body">
                        <h5 className="card-title">{profile.displayname}</h5>
                        <p className="card-text">{profile.bio}</p>
                        <div className=" mb-3">

                            <div className="card-text"><small className="text-muted">0 followers</small></div>
                            {profile.username === userCurrent.username ? null :
                                (userCurrent.following.includes(profile.username) ? <button className="btn btn-outline-primary">Unfollow</button> :
                                    <button onClick={handleFollow} className="btn btn-outline-primary">Follow</button>)}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileInfo
