import React, { useState } from "react"

const ProfileInfo = () => {
    const [follow, setFollow] = useState(false)

    const handleFollow = () => {
        setFollow(!follow)
    }


    return (
        <div className=" row">
            <div className=" col-xxl-6 m-auto">
                <div class="card mb-3 shadow-sm p-3 border border-primary rounded">
                    <div class="card-body">
                        <h5 class="card-title">Bui Thanh Duoc</h5>
                        <p class="card-text">I'm a student</p>
                        <div className=" mb-3">
                            <div class="card-text"><small class="text-muted">1000 followers</small></div>
                            {follow ? <button onClick={handleFollow} class="btn btn-primary btn-s">Follow</button>
                                : <button onClick={handleFollow} class="btn btn-primary btn-sm d-inline">Unfollow</button>}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileInfo
