import React, { useState } from "react"

const CreatePost = () => {
    const [content, setContent] = useState('')
    const [image, setImage] = useState([])

    const handleUpImage = (e) => {
        const listFile = e.target.files
        const listImage = []
        for (let i = 0; i < listFile.length; i++) {
            listImage.push(new Blob([listFile[i]], { type: listFile[i].type }))
        }
        setImage(listImage)
        e.target.value = null
    }

    const listImages = image.map((item, index) => {
        const handleDelete = (index) => {
            const listImage = [...image]
            listImage.splice(index, 1)
            setImage(listImage)
        }
        return (
            <img onClick={() => handleDelete(index)} height="150px" width="150px" src={URL.createObjectURL(item)} alt="" key={index} />
        )
    })

    return (
        <div className=" row mb-5">
            <div className=" col-xxl-6 m-auto">
                <div className=" card  shadow-sm p-3 border border-primary rounded">
                    <div className=" card-body">
                        <div className=" mb-3">
                            <textarea onChange={e => setContent(e.target.value)} value={content} className=" form-control" placeholder="Bạn đang nghĩ gì?"></textarea>
                        </div>
                        <div className=" mb-3">
                            {image && listImages}
                        </div>
                        <input multiple type="file" className=" form-control-file" onChange={(e) => handleUpImage(e)} />
                        <button className="btn btn-primary float-end">Đăng</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CreatePost
