import React, { useState } from "react"
import { useSelector } from "react-redux"
import axios from "axios"

const CreatePost = () => {
    const account = useSelector(state => state.account)
    const [content, setContent] = useState('')
    const [image, setImage] = useState([])

    const handleUpPost = async () => {
        // const formData = new FormData()
        // image.forEach(file => {
        //     formData.append('images', file)
        // })
        // formData.append('content', content)
        // formData.append('username', account.username)
        // formData.append('displayname', account.displayname)
        // const rs = await axios.post('/posts/create', formData,{headers: {'Content-Type': 'multipart/form-data'}})
        const imaged = image.map(file => file.name)
        const blob = new Blob(image[0], { type: 'image/jpeg' })
        const rs = await axios.post('/posts/create', { blob})
        if (rs.data.success) {
            alert(rs.data.message)
            setContent('')
            setImage([])
        } else {
            alert(rs.data.message)
        }
    }

    const handleUpImage = (e) => {
        const listFile = e.target.files
        const listImage = []
        for (let i = 0; i < listFile.length; i++) {
            listImage.push(listFile[i])
        }
        setImage(listImage)
        e.target.value = null
    }

    const listImages = image.map((item, index) => {
        const handleDelete = (index) => {
            const listImage = [...image]
            listImage.splice(index, 1)
            setImage(listImage)
        }
        return (
            <img onClick={() => handleDelete(index)} height="150px" width="150px" src={URL.createObjectURL(item)} alt="" key={index} />
        )
    })

    return (
        <div className=" row mb-5">
            <div className=" col-xxl-6 m-auto">
                <div className=" card  shadow-sm p-3 border border-primary rounded">
                    <div className=" card-body">
                        <div className=" mb-3">
                            <textarea onChange={e => setContent(e.target.value)} value={content} className=" form-control" placeholder="Bạn đang nghĩ gì?"></textarea>
                        </div>
                        <div className=" mb-3">
                            {image && listImages}
                        </div>
                        <input accept="image/jpeg,image/png,image/webp,image/gif" multiple type="file" className=" form-control-file" onChange={(e) => handleUpImage(e)} />
                        <button onClick={handleUpPost} className="btn btn-primary float-end">Đăng</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CreatePost
