import React from "react"
import { Navigate, useNavigate } from 'react-router-dom'
const ListPosts = ({ posts }) => {


    const listPosts = posts.map((post) => {
        const { id, title, body } = post
        const handleClick = (id) => {
            return <Navigate to={`/posts/${id}`} />
        }

        return (
            <div onClick={() => handleClick(post.id)} className=" card" key={post.id}>
                <div className=" card-body">
                    <h5 className="card-title">{post.displayName}</h5>
                    <p className="card-text">{post.content}</p>
                    <p>{post.likes} thich - {post.comments} binh luan</p>
                </div>
            </div>)
    })


    return (
        <div className="row">
            <div className="col-xxl-6 m-auto">
                {listPosts}
            </div>
        </div>
    )

}


export default ListPosts
