import React, { useState } from "react"
import { useSelector } from "react-redux"
import axios from "axios"

const CreatePost = () => {
    const account = useSelector(state => state.account)
    const [content, setContent] = useState('')
    const [images, setImages] = useState([])

    const handleUpPost = async () => {

    }

    const handleUpImage = (e) => {
        const file = e.target.files
        console.log(file)
        let images = []
        
        for (let i = 0; i < file.length; i++) {
            let reader = new FileReader()
            reader.readAsDataURL(file[i])
            reader.onload = async () => {
                await images.push(reader.result)
            }
        }
        setImages(images)
    }

    const listImages = images.map((item, index) => {
        const handleDelete = (index) => {
            const listImage = [...images]
            listImage.splice(index, 1)
            setImages(listImage)
        }
        return (
            <img onClick={() => handleDelete(index)} height="150px" width="150px" src={item} alt="" key={index} />
        )
    })

    return (
        <div className=" row mb-5">
            <div className=" col-xxl-6 m-auto">
                <div className=" card  shadow-sm p-3 border border-primary rounded">
                    <div className=" card-body">
                        <div className=" mb-3">
                            <textarea onChange={e => setContent(e.target.value)} value={content} className=" form-control" placeholder="Bạn đang nghĩ gì?"></textarea>
                        </div>
                        <div className=" mb-3">
                            {images && listImages}
                        </div>
                        <input accept="image/jpeg,image/png,image/webp,image/gif" multiple type="file" className=" form-control-file" onChange={(e) => handleUpImage(e)} />
                        <button onClick={handleUpPost} className="btn btn-primary float-end">Đăng</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CreatePost
