import React, { useState } from "react"
import { Navigate } from 'react-router-dom'
import { useSelector } from 'react-redux'


const HomePage = () => {
    const account = useSelector(state => state.account)
    //-----------------------------Create-Post--------------------------------
    const [error_create_post, setError_create_post] = useState({error: false, message: ''})
    const [content, setContent] = useState('')
    const hanldeCreatePost = () => {
        if (!content) {
            setError_create_post({error: true, message: 'Please enter content'})
        }
     }
    //-----------------------------List-Posts----------------------------------------------------
    

    if (account.username === null) return <Navigate to='/login' />
    return (
        <>
            <div className=" row mb-5">
                <div className=" col-xxl-6 m-auto">
                    <div className=" card  shadow-sm p-3 border border-primary rounded">
                        <div className=" card-body">
                            {error_create_post.error && <div className=" mb-3"><div className=" alert alert-danger">{error_create_post.message}</div></div> }
                            <div className=" mb-3">
                                <textarea onChange={e => { setContent(e.target.value) }} value={content} className=" form-control" placeholder="Bạn đang nghĩ gì?"></textarea>
                            </div>
                            <button onClick={hanldeCreatePost} className="btn btn-primary float-end">Đăng</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className=" row mb-5">
                <div className=" col-xxl-6 m-auto">
                    <div className=" card  shadow-sm p-3 border border-primary rounded">
                        <div className=" card-body"></div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default HomePage
