import React from "react"
import { useNavigate } from 'react-router-dom'
import ListPhotos from "../ListPhotos"
import './ListPost.css'

const ListPosts = ({ posts }) => {
    const navigate = useNavigate()

    const ItemPost = posts.map((post) => {
        const handleClick = (id) => {
            navigate(`/posts/${id}`)
        }

        return (
            <div onClick={() => handleClick(post.id)} className=" list-post card border border-primary mb-3" key={post.id}>
                <div className=" card-body">
                    <h5 className="card-title">{post.displayName}</h5>
                    <p className="card-text">{post.content}</p>
                    <ListPhotos />
                    <p>{post.likes} thich - {post.comments} binh luan</p>
                </div>
            </div>)
    })


    return (
        <div className="row">
            <div className="col-xxl-6 m-auto">
                {ItemPost}
            </div>
        </div>
    )
}


export default ListPosts
